import { useState } from "react";
import "../ExpenseMain/ExpenseDate.css";
import Card from "../UI/Card";

const ExpenseDate = (props) => {
  console.log(props.date);
  const day = props.date.toLocaleString("en-US", { day: "2-digit" });
  const month = props.date.toLocaleString("en-US", { month: "long" });
  const year = props.date.getFullYear();
const [d1,setDate]=useState(day)
const [d2,setDate2]=useState(month)
const [d3,setDate3]=useState(year)
  const updateDate=()=>{
    setDate()
  }

  return (
    <Card className="expense-date">
      {/* <div className="expense-date"> */}
      <div className="expense-date__day">{d1}</div>

      <div className="expense-date__month">{d2}</div>

      <div className="expense-date__year"> {d3}</div>
      {/* </div> */}
      <button onClick={updateDate}>Update Date</button>
    </Card>

    
  );
};

export default ExpenseDate;
